/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #20bfa9;
  --color-accent-2: #ff794d;

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #e1ecf0;
}

/* Root element (use instead of `body`) */
#page {
  font-family: system-ui, sans-serif;
  color: #1d263a;
  line-height: 1.2;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 1250px;
  margin: 0 auto;
  padding: 5rem 2rem;
}

a.link {
  line-height: 1.3;
  font-weight: 500;
  border-bottom: 2px solid;
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.1;
  font-weight: 600;
}

.subheading {
  font-weight: 400;
  line-height: 1.5;
  opacity: 90%;
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  color: white;
  font-weight: 500;
  background: var(--color-accent, rebeccapurple);
  border-radius: 3rem;
  padding: 15px 25px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.section .content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 5rem 2rem;

  p {
    padding: 0.25rem 0;
    line-height: 1.5;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
  }

  h1 {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
  }

  h2 {
    font-size: 2.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
  }

  h3 {
    font-size: 1.75rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container { /* Defined in Site CSS */
  display: grid;
