section {
  background: #f9fafb;
}

.section-container {

} 

.heading {
  max-width: 700px;
  margin: 0 auto;
  text-align: center;
  margin-bottom: 3rem;
}

ul.cards {
  display: grid;
  place-items: start;
  place-content: center;
  grid-template-columns: repeat(auto-fit, minmax(100px, 350px));
  gap: 2rem;

  li {
    background: white;
    padding: 2rem;
    border: 1px solid var(--border-color, #eee);
    border-radius: var(--border-radius);
    display: grid;

    img {
      max-width: 120px;
      aspect-ratio: 5 / 1;
      object-fit: cover;
      border-radius: var(--border-radius);
      width: 100%; /* For Safari */
      margin-bottom: 2rem;
    }

    .short-quote {
      font-weight: 600;
      font-size: 1.5rem;
      line-height: 2rem;
    }

    .long-quote {
      font-weight: 400;
      font-size: 1rem;
      line-height: 1.5;
      opacity: 0.8;
    }

    .info {
      display: grid;
      gap: 0.75rem;

      .details {
        display: grid;

        .name {
          font-weight: 600;
          font-size: 1rem;
          line-height: 1.75rem;
        }

        .title {
          font-weight: 400;
          font-size: 0.75rem;
          line-height: 1.25rem;
          opacity: 0.6;
        }
      }
    }
  }
}

.section-container {
  display: grid;
  gap: clamp(50px, 10vw, 200px);

  @media (min-width: 700px) {
    grid-template-columns: 1fr 1fr;
  }
}

.card {
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow);
  overflow: hidden;
  max-width: 450px;

  img {
    width: 100%;
    object-fit: cover;
  }

  .card-content {
    padding: 2rem;
    display: grid;


    @media (min-width: 700px) {
      padding: 3rem;
    }

    .title {
      font-weight: 600;
      font-size: 1.5rem;
      line-height: 2rem;
      margin-bottom: 1rem;
    }

    .description {
      font-weight: 400;
      line-height: 1.6;
      margin-bottom: 1.5rem;
    }

    .button {
      text-align: center;
    }
  }
}

.main {
  display: grid;
  place-content: center start;
  gap: 2rem;
  max-width: 450px;


